package com.example.tumblrmenuimport android.content.Intentimport androidx.appcompat.app.AppCompatActivityimport android.os.Bundleimport kotlinx.android.synthetic.main.activity_main.*class MainActivity : AppCompatActivity() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_main)        newpost.setOnClickListener {            var intent = Intent(this, MenuActivity::class.java)            startActivity(intent)            overridePendingTransition(android.R.anim.fade_in, android.R.anim.fade_out)        }    }}